<script type="text/javascript">
var random_images_array = ["data:image/png;base64,", "https://www.jqueryscript.net/images/Simplest-Responsive-jQuery-Image-Lightbox-Plugin-simple-lightbox.jpg", "https://i1.wp.com/netino.fr/wp-content/uploads/2017/08/exemple-moderation-3.png?resize=300%2C63", "https://i2.wp.com/netino.fr/wp-content/uploads/2017/08/exemple-moderation-2.png?resize=300%2C87"];

function getRandomImage() {
  var randomNumber = Math.floor(Math.random() * random_images_array.length);
  document.canvas.src = random_images_array[randomNumber];
}
window.onload = getRandomImage();

</script>
